import React, { useState } from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import { useDispatch,useSelector } from 'react-redux';
import { deleteComment } from '../../lib/api/posts';
import { listComments } from '../../modules/post';

const CommentListBlock = styled(Responsive)`
  margin-top: 3rem;
`;

const CommentItemBlock = styled.div`
  padding-top: 1rem;
  padding-bottom: 1rem;

  &:first-child {
    padding-top: 0;
  }

  & + & {
    border-top: 1px solid ${palette.gray[2]};
  }

  h4 {
    margin: 0;
    margin-bottom: 0.5rem;
    color: ${palette.gray[8]};
  }

  p {
    margin-top: 0.5rem;
  }
`;

const CommentItem = ({ comment, onRemove }) => {
  //console.log(comment);
  const { _id, user, text, createdAt } = comment;
  const { luser } = useSelector(({ user }) => ({
    luser: luser.user,
  }));
  console.log(luser);
  const dispatch = useDispatch();
  const handleRemove = async () => {
    //console.log(comment);
    //return false;
    try {
      await deleteComment({ postId: comment.post, commentId: _id });
      onRemove(_id);
      dispatch(listComments(comment.post)); // 댓글 목록 불러오기
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <CommentItemBlock> 
      <p>{text}</p>
      <p>작성자 : {user.username} 작성날짜 :{new Date(createdAt).toLocaleDateString()}</p>
      <button onClick={handleRemove}>삭제</button>
    </CommentItemBlock>
  );
};

const CommentList = ({ comments }) => {
  //console.log('aaa');
  const [commentList, setCommentList] = useState(comments);

  const handleRemove = (id) => {
    setCommentList(commentList.filter((comment) => comment._id !== id));
  };
  return (
    <CommentListBlock>
      <p>댓글 리스트</p>
      {comments.map(comment => (
        <CommentItem key={comment._id} comment={comment} onRemove={handleRemove}/>
      ))}
    </CommentListBlock>
  );
};

export default CommentList;
