import React, { useState } from 'react';
import styled from 'styled-components';


const CommentInputBlock = styled.div`
  margin-top: 3rem;
  padding-top: 3rem;
  border-top: 1px solid blue;
  h3 {
    margin-bottom: 1rem;
  }
  textarea {
    width: 100%;
    min-height: 10rem;
    font-size: 1rem;
    margin-bottom: 1rem;
    padding: 1rem;
    border-radius: 4px;
    border: 1px solid blue;
    outline: none;
    resize: none;
  }
  button {
    float: right;
    padding: 0.5rem 1rem;
    color: white;
    background: blue;
    border-radius: 4px;
    border: none;
    outline: none;
    cursor: pointer;
    &:hover {
      background: blue;
    }
  }
`;

const CommentInput = ({ onSubmit }) => {
  const [value, setValue] = useState('');

  const onChange = e => {
    setValue(e.target.value);
  };

  const handleSubmit = () => {
    onSubmit(value);
    setValue('');
  };

  return (
    <CommentInputBlock>
      <h3>댓글 쓰기</h3>
      <textarea
        placeholder="댓글을 입력하세요."
        value={value}
        onChange={onChange}
      />
      <button onClick={handleSubmit}>댓글 등록</button>
    </CommentInputBlock>
  );
};

export default CommentInput;
