import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { readPost, unloadPost } from '../../modules/post';
import PostViewer from '../../components/post/PostViewer';
import PostActionButtons from '../../components/post/PostActionButtons';
import { setOriginalPost } from '../../modules/write';
import { removePost } from '../../lib/api/posts';
import CommentList from '../../components/post/CommentList';
import CommentInput from '../../components/post/CommentInput';
import { initialize, changeField } from '../../modules/comment';

const PostViewerContainer = ({ match, history }) => {
  const { postId } = match.params;
  const dispatch = useDispatch();
  const { post, error, loading, user, comment } = useSelector(({ post, loading, user, comment }) => ({
    post: post.post,
    error: post.error,
    loading: loading['post/READ_POST'],
    user: user.user,
    comment: comment,
  }));

  const onRemove = async () => {
    try {
      await removePost(postId);
      history.push('/'); // 홈으로 이동
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    dispatch(readPost(postId));
    return () => {
      dispatch(unloadPost());
      dispatch(initialize());
    };
  }, [dispatch, postId]);

  const onEdit = () => {
    dispatch(setOriginalPost(post));
    history.push('/write');
  };

  const onSubmitComment = (e) => {
    const { contents } = comment;

    // Make sure the comment is not empty
    if (!contents) {
      alert('Please enter a comment.');
      return;
    }
  
    // Submit the comment to the server
    const data = {
      postId,
      contents,
    };
    // Replace the `console.log` statement with a call to your API function
    postComment(data)
      .then((response) => {
        console.log(response);
        dispatch(readPost(postId)); // reload the post to display the new comment
        dispatch(initialize()); // reset the comment state to its initial values
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onChangeCommentField = (e) => {
    const { value } = e.target;
    dispatch(changeField({ key: 'contents', value }));
  };

  return (
    <>
    <PostViewer
      post={post}
      loading={loading}
      error={error}
      actionButtons={<PostActionButtons onEdit={onEdit} onRemove={onRemove} />}
      ownPost={user && user.id === post && post.id}
    >
     
      
    </PostViewer>
    <CommentList />
    <CommentInput comment={comment} onSubmitComment={onSubmitComment} onChangeComment={onChangeCommentField} />
    </>
  );
};

export default withRouter(PostViewerContainer);
