import React, { useState } from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import { deleteComment } from '../../lib/api/posts';

const CommentListBlock = styled(Responsive)`
  margin-top: 3rem;
`;

const CommentItemBlock = styled.div`
  padding-top: 1rem;
  padding-bottom: 1rem;

  &:first-child {
    padding-top: 0;
  }

  & + & {
    border-top: 1px solid ${palette.gray[2]};
  }

  h4 {
    margin: 0;
    margin-bottom: 0.5rem;
    color: ${palette.gray[8]};
  }

  p {
    margin-top: 0.5rem;
  }
`;

const CommentItem = ({ comment,postId, onRemove }) => {
  //console.log(comment);
  const { id,user, text, createdAt } = comment;

  const handleRemove = async () => {
    try {
      await deleteComment({ postId, commentId: id });
      onRemove(id);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <CommentItemBlock> 
      <p>{text}</p>
      <p>작성자 : {user.username} 작성날짜 :{new Date(createdAt).toLocaleDateString()}</p>
      <button onClick={handleRemove}>삭제</button>
    </CommentItemBlock>
  );
};

const CommentList = ({ postId, comments }) => {
  //console.log('aaa');
  const [commentList, setCommentList] = useState(comments);

  const handleRemove = (id) => {
    setCommentList(commentList.filter((comment) => comment.id !== id));
  };

  return (
    <CommentListBlock>
      <p>댓글 리스트</p>
      {comments.map(comment => (
        <CommentItem key={comment.id} comment={comment} postId={postId} onRemove={handleRemove} />
      ))}
    </CommentListBlock>
  );
};

export default CommentList;
