import React, { useState } from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import { useDispatch} from 'react-redux';
import { deleteComment,writeCommentReply } from '../../lib/api/posts';
import { listComments } from '../../modules/post';

const CommentListBlock = styled(Responsive)`
  margin-top: 3rem;
`;

const CommentItemBlock = styled.div`
  padding-top: 1rem;
  padding-bottom: 1rem;

  &:first-child {
    padding-top: 0;
  }

  & + & {
    border-top: 1px solid ${palette.gray[2]};
  }

  h4 {
    margin: 0;
    margin-bottom: 0.5rem;
    color: ${palette.gray[8]};
  }

  p {
    margin-top: 0.5rem;
  }
`;

const CommentItem = ({ comment, onRemove ,loginuser}) => {
  
  const { _id, user, text, createdAt,replies  } = comment;
  const [replyText, setReplyText] = useState('');
  const [replyList] = useState(replies);

  const dispatch = useDispatch();
  const handleRemove = async (idx) => {
   
    if(comment.user._id === loginuser._id){
     console.log(idx);
     return;
      try {
        await deleteComment({ postId: comment.post, commentId: _id });
        onRemove(_id);
        dispatch(listComments(comment.post)); // 댓글 목록 불러오기
      } catch (e) {
        console.log(e);
      }

    }else{
      alert('본인 댓글만 삭제하실 수 있습니다.');
      return false;
    }   
  };





  const handleReplySubmit = async () => {
    if (!replyText) {
      alert('대댓글을 입력해주세요.');
      return;
    }
    //console.log(_id);
    try {
      await writeCommentReply({
        postId: comment.post,
        text: replyText,
        parentCommentId: _id, // 부모 댓글의 ID를 전달
      });
      setReplyText('');
      dispatch(listComments(comment.post)); // 댓글 목록 불러오기
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <CommentItemBlock> 
      <p>{text}</p>
      <p>작성자 : {user.username} 작성날짜 :{new Date(createdAt).toLocaleDateString()}</p>
      <button onClick={handleRemove(_id)}>삭제</button>

      {replyList.map((reply) => (
        <CommentItemBlock key={reply._id}>
          <p>{reply.text}</p>
          <p>
            작성자: {reply.user.username} 작성날짜:{' '}
            {new Date(reply.createdAt).toLocaleDateString()}
          </p>
          {reply.user._id === loginuser._id && (
            <button onClick={handleRemove(reply._id)}>삭제</button>
          )}
        </CommentItemBlock>
      ))}
      {/* 대댓글 작성 폼 */}
      <div style={{ marginLeft: '2rem' }}>
        <textarea
          placeholder="대댓글을 입력하세요"
          value={replyText}
          onChange={(e) => setReplyText(e.target.value)}
        />
        <button onClick={handleReplySubmit}>등록</button>
      
      </div>
    </CommentItemBlock>
  );
};

const CommentList = ({ comments,loginuser }) => {
  //console.log('aaa');
  const [commentList, setCommentList] = useState(comments);

  const handleRemove = (id) => {
    setCommentList(commentList.filter((comment) => comment._id !== id));
  };
  return (
    <CommentListBlock>
      <p>댓글 리스트</p>
      {comments.map(comment => (
        <CommentItem key={comment._id} comment={comment} loginuser={loginuser} onRemove={handleRemove}/>
      ))}
    </CommentListBlock>
  );
};

export default CommentList;
