import React, { useEffect, useRef } from 'react';
import qs from 'qs';
import { useDispatch, useSelector } from 'react-redux';
import PostList from '../../components/posts/PostList';
import { listPosts } from '../../modules/posts';

const PostListContainer = ({ location }) => {
  const dispatch = useDispatch();
  const { posts, error, loading, user, hasMorePosts } = useSelector(
    ({ posts, loading, user }) => ({
      posts: posts.posts,
      error: posts.error,
      loading: loading['posts/LIST_POSTS'],
      user: user.user,
      hasMorePosts: posts.hasMorePosts,
    }),
  );
  const page = useRef(1);
  const targetRef = useRef(null);

  const loadMorePosts = () => {
    if (!loading && hasMorePosts) {
      const { tag, username } = qs.parse(location.search, {
        ignoreQueryPrefix: true,
      });
      dispatch(listPosts({ tag, username, page: page.current + 1 }));
      page.current += 1;
    }
  };

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      const entry = entries[0];
      if (entry.isIntersecting) {
        loadMorePosts();
      }
    }, { threshold: 1 });

    if (targetRef.current) {
      observer.observe(targetRef.current);
    }

    return () => {
      if (targetRef.current) {
        observer.unobserve(targetRef.current);
      }
    };
  }, [loadMorePosts]);

  return (
    <>
      <PostList
        loading={loading && !posts}
        error={error}
        posts={posts}
        showWriteButton={user}
      />
      {loading && posts && <div>로딩 중...</div>}
      {!loading && hasMorePosts && <div ref={targetRef}>더보기</div>}
    </>
  );
};

export default PostListContainer;
